// Country to language mapping based on IP geolocation
const countryToLanguage = {
    'IT': 'it',
    'FR': 'fr',
    'DE': 'de',
    'ES': 'es',
    'RU': 'ru',
    'US': 'en',
    'GB': 'en',
    'CA': 'en',
    'AU': 'en',
    'NZ': 'en',
    'IE': 'en',
    'ZA': 'en'
};

// Default language
let currentLanguage = 'en';

// Detect language based on IP geolocation
async function detectLanguageFromIP() {
    try {
        // Use ipapi.co free API for geolocation
        const response = await fetch('https://ipapi.co/json/');
        const data = await response.json();
        
        if (data.country_code) {
            const detectedLang = countryToLanguage[data.country_code] || 'en';
            return detectedLang;
        }
    } catch (error) {
        console.log('IP geolocation failed, using browser language:', error);
    }
    
    // Fallback to browser language
    const browserLang = navigator.language || navigator.userLanguage;
    const langCode = browserLang.split('-')[0].toLowerCase();
    
    // Map browser language to supported languages
    const langMap = {
        'it': 'it',
        'fr': 'fr',
        'de': 'de',
        'es': 'es',
        'ru': 'ru',
        'en': 'en'
    };
    
    return langMap[langCode] || 'en';
}

// Translate all elements with data-translate attribute
function translatePage(lang) {
    currentLanguage = lang;
    document.documentElement.lang = lang;
    
    // Get translations for current language
    const langTranslations = translations[lang] || translations['en'];
    
    // Translate all elements with data-translate attribute
    document.querySelectorAll('[data-translate]').forEach(element => {
        const key = element.getAttribute('data-translate');
        const translation = langTranslations[key];
        
        if (translation) {
            if (element.tagName === 'INPUT' || element.tagName === 'BUTTON') {
                element.textContent = translation;
            } else {
                element.innerHTML = translation;
            }
        }
    });
}

// Initialize translation on page load
document.addEventListener('DOMContentLoaded', async () => {
    const detectedLang = await detectLanguageFromIP();
    translatePage(detectedLang);
    
    // Store language preference in localStorage
    localStorage.setItem('preferredLanguage', detectedLang);
});

// Export for use in script.js
if (typeof module !== 'undefined' && module.exports) {
    module.exports = { translations, translatePage, currentLanguage };
}

